package samba.schema.content.ssz.blockheader;

import static org.junit.jupiter.api.Assertions.assertEquals;

import samba.domain.content.ContentProofConstants;

import java.util.ArrayList;
import java.util.List;

import org.apache.tuweni.bytes.Bytes;
import org.apache.tuweni.bytes.Bytes32;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import tech.pegasys.teku.infrastructure.unsigned.UInt64;

public class BlockProofHistoricalRootsContainerTest {

  List<Bytes32> beaconBlockProofHistoricalRoots;
  Bytes32 blockRoot;
  List<Bytes32> executionBlockProof;
  UInt64 slot;

  @BeforeEach
  public void setup() {
    this.beaconBlockProofHistoricalRoots = new ArrayList<>();
    for (int i = 0; i < ContentProofConstants.BEACON_BLOCK_PROOF_HISTORICAL_ROOTS_VECTOR_SIZE; i++)
      this.beaconBlockProofHistoricalRoots.add(Bytes32.repeat((byte) i));
    this.blockRoot = Bytes32.repeat((byte) 1);
    this.executionBlockProof = new ArrayList<>();
    for (int i = 0; i < ContentProofConstants.EXECUTION_BLOCK_PROOF_SIZE; i++)
      this.executionBlockProof.add(Bytes32.repeat((byte) i));
    this.slot = UInt64.valueOf(1234);
  }

  @Test
  public void testSszDecode() {
    BlockProofHistoricalRootsContainer blockProofHistoricalRootsContainer =
        BlockProofHistoricalRootsContainer.decodeBytes(
            Bytes.fromHexString(
                ""));
    assertEquals(
        blockProofHistoricalRootsContainer.getBeaconBlockProofHistoricalRoots(),
        this.beaconBlockProofHistoricalRoots);
    assertEquals(blockProofHistoricalRootsContainer.getBlockRoot(), this.blockRoot);
    assertEquals(
        blockProofHistoricalRootsContainer.getExecutionBlockProof(), this.executionBlockProof);
    assertEquals(blockProofHistoricalRootsContainer.getSlot(), this.slot);
  }

  @Test
  public void testSszEncode() {
    BlockProofHistoricalRootsContainer blockProofHistoricalRootsContainer =
        new BlockProofHistoricalRootsContainer(
            this.beaconBlockProofHistoricalRoots,
            this.blockRoot,
            this.executionBlockProof,
            this.slot);
    Bytes encodedBlockProofHistoricalRootsContainer =
        blockProofHistoricalRootsContainer.sszSerialize();
    assertEquals(
        encodedBlockProofHistoricalRootsContainer,
        Bytes.fromHexString(
            ""));
  }
}
